    private void jButton_imgMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton_imgMouseClicked
        // TODO add your handling code here:
        Icon icon4 = new ImageIcon(new ImageIcon(getClass().getResource("/Assets/img1.jpg")).getImage().getScaledInstance(label_img1.getWidth(), label_img1.getHeight(), WIDTH));
        Icon icon1 = new ImageIcon(new ImageIcon(getClass().getResource("/Assets/image1.jpg")).getImage().getScaledInstance(label_img1.getWidth(), label_img1.getHeight(), WIDTH));
        Icon icon2 = new ImageIcon(new ImageIcon(getClass().getResource("/Assets/image2.png")).getImage().getScaledInstance(label_img1.getWidth(), label_img1.getHeight(), WIDTH));
        Icon icon3 = new ImageIcon(new ImageIcon(getClass().getResource("/Assets/image3.png")).getImage().getScaledInstance(label_img1.getWidth(), label_img1.getHeight(), WIDTH));
        
        Icon[] icons = new Icon[4];
        
        icons[0] = icon1;
        icons[1] = icon2;
        icons[2] = icon3;
        icons[3] = icon4;
        
        if (current_img < icons.length-1) {
            current_img += 1;
            ChangeImage(icons, current_img);
        } else if (current_img == icons.length-1) {
            current_img = 0;
            ChangeImage(icons, current_img);
        }
        
    }//GEN-LAST:event_jButton_imgMouseClicked

