    private void jButton3MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton3MouseClicked
        // TODO add your handling code here:
        String get_startspeed, get_time;
        get_startspeed = txf_startspeed1.getText();
        get_time = txf_time1.getText();

        Double vi = null, t = null;
        
        if (ValidNumber(get_startspeed)) { // evaluar velocidad inicial
            vi = Double.valueOf(get_startspeed);
            txf_startspeed1.setBackground(Color.white);
        } else {
            JOptionPane.showMessageDialog(this,error_message);
            txf_startspeed1.setBackground(Color.red);
        }
        
        if (ValidNumber(get_time)) { // evaluar tiempo
            t = Double.valueOf(get_time);
            txf_time1.setBackground(Color.white);
        } else {
            JOptionPane.showMessageDialog(this,error_message);
            txf_time1.setBackground(Color.red);
        }

        if (ValidNumber(get_startspeed) && ValidNumber(get_time)) {
            Double vf = vi + (G * t);
            JOptionPane.showMessageDialog(this,"La Velocidad final es: "+String.format("%.2f", vf)+"m/s");
        }
    }//GEN-LAST:event_jButton3MouseClicked

