    private void jButton5MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton5MouseClicked
        // TODO add your handling code here:
        String get_height, get_startspeed;
        get_startspeed = txf_startspeed3.getText();
        get_height = txf_height3.getText();
        
        Double vi = null, h = null;
        
        if (ValidNumber(get_startspeed)) { // evaluar velocidad inicial
            vi = Double.valueOf(get_startspeed);
            txf_startspeed3.setBackground(Color.white);
        } else {
            JOptionPane.showMessageDialog(this,error_message);
            txf_startspeed3.setBackground(Color.red);
        }
        
        if (ValidNumber(get_height)) { // evaluar tiempo
            h = Double.valueOf(get_height);
            txf_height3.setBackground(Color.white);
        } else {
            JOptionPane.showMessageDialog(this,error_message);
            txf_height3.setBackground(Color.red);
        }
        
        if (ValidNumber(get_startspeed) && ValidNumber(get_height)) {
            Double Vf2 = (vi*vi) + 2*(G * h);
            Double Vf = Math.sqrt(Vf2);
            
            JOptionPane.showMessageDialog(this,"La Velocidad final al cuadrado es: "+String.format("%.2f", Vf2)+"m/s");
            JOptionPane.showMessageDialog(this,"La Velocidad final es: "+String.format("%.2f", Vf)+"m/s");
        }
    }//GEN-LAST:event_jButton5MouseClicked

